import { By } from 'selenium-webdriver';
import { DriverLike, register } from '../../';
import { OjSwipeActions } from './OjSwipeActions';
export { OjSwipeActions };

/**
 * Retrieve an instance of [[OjSwipeActions]].
 * @example
 * ```javascript
 * import { ojSwipeActions } from '@oracle/oraclejet-webdriver/elements';
 * const el = await ojSwipeActions(driver, By.id('my-oj-swipe-actions'));
 * ```
 * @param driver A WebDriver/WebElement instance from where the element will be
 * searched. If WebDriver is passed, the element will be searched globally in the
 * document. If WebElement is passed, the search will be relative to this element.
 * @param by The locator with which to find the element
 */
export async function ojSwipeActions(driver: DriverLike, by: By): Promise<OjSwipeActions> {
  const webEl = await driver.findElement(by);
  // Check that the element is of type OjSwipeActions
  if (!(webEl instanceof OjSwipeActions)) {
    const tagName = await webEl.getTagName();
    let supplemental = '';
    if (tagName === OjSwipeActions.name) {
      supplemental = 'You likely have multiple installations of this package.';
    }
    throw Error(
      `ojSwipeActions(${by}) created ${webEl.constructor.name} for <${tagName}>, but expected ${OjSwipeActions.name}. ${supplemental}`
    );
  }
  return webEl;
}

register('oj-swipe-actions', OjSwipeActions);
