import { By } from 'selenium-webdriver';
import { DriverLike, register } from '../../';
import { OjSwitch } from './OjSwitch';
export { OjSwitch };

/**
 * Retrieve an instance of [[OjSwitch]].
 * @example
 * ```javascript
 * import { ojSwitch } from '@oracle/oraclejet-webdriver/elements';
 * const el = await ojSwitch(driver, By.id('my-oj-switch'));
 * ```
 * @param driver A WebDriver/WebElement instance from where the element will be
 * searched. If WebDriver is passed, the element will be searched globally in the
 * document. If WebElement is passed, the search will be relative to this element.
 * @param by The locator with which to find the element
 */
export async function ojSwitch(driver: DriverLike, by: By): Promise<OjSwitch> {
  const webEl = await driver.findElement(by);
  // Check that the element is of type OjSwitch
  if (!(webEl instanceof OjSwitch)) {
    const tagName = await webEl.getTagName();
    let supplemental = '';
    if (tagName === OjSwitch.name) {
      supplemental = 'You likely have multiple installations of this package.';
    }
    throw Error(
      `ojSwitch(${by}) created ${webEl.constructor.name} for <${tagName}>, but expected ${OjSwitch.name}. ${supplemental}`
    );
  }
  return webEl;
}

register('oj-switch', OjSwitch);
