import { By } from 'selenium-webdriver';
import { DriverLike, register } from '../../';
import { OjTabBar } from './OjTabBar';
export { OjTabBar };

/**
 * Retrieve an instance of [[OjTabBar]].
 * @example
 * ```javascript
 * import { ojTabBar } from '@oracle/oraclejet-webdriver/elements';
 * const el = await ojTabBar(driver, By.id('my-oj-tab-bar'));
 * ```
 * @param driver A WebDriver/WebElement instance from where the element will be
 * searched. If WebDriver is passed, the element will be searched globally in the
 * document. If WebElement is passed, the search will be relative to this element.
 * @param by The locator with which to find the element
 */
export async function ojTabBar(driver: DriverLike, by: By): Promise<OjTabBar> {
  const webEl = await driver.findElement(by);
  // Check that the element is of type OjTabBar
  if (!(webEl instanceof OjTabBar)) {
    const tagName = await webEl.getTagName();
    let supplemental = '';
    if (tagName === OjTabBar.name) {
      supplemental = 'You likely have multiple installations of this package.';
    }
    throw Error(
      `ojTabBar(${by}) created ${webEl.constructor.name} for <${tagName}>, but expected ${OjTabBar.name}. ${supplemental}`
    );
  }
  return webEl;
}

register('oj-tab-bar', OjTabBar);
