import { By } from 'selenium-webdriver';
import { DriverLike, register } from '../../';
import { OjTable } from './OjTable';
export { OjTable };

/**
 * Retrieve an instance of [[OjTable]].
 * @example
 * ```javascript
 * import { ojTable } from '@oracle/oraclejet-webdriver/elements';
 * const el = await ojTable(driver, By.id('my-oj-table'));
 * ```
 * @param driver A WebDriver/WebElement instance from where the element will be
 * searched. If WebDriver is passed, the element will be searched globally in the
 * document. If WebElement is passed, the search will be relative to this element.
 * @param by The locator with which to find the element
 */
export async function ojTable(driver: DriverLike, by: By): Promise<OjTable> {
  const webEl = await driver.findElement(by);
  // Check that the element is of type OjTable
  if (!(webEl instanceof OjTable)) {
    const tagName = await webEl.getTagName();
    let supplemental = '';
    if (tagName === OjTable.name) {
      supplemental = 'You likely have multiple installations of this package.';
    }
    throw Error(
      `ojTable(${by}) created ${webEl.constructor.name} for <${tagName}>, but expected ${OjTable.name}. ${supplemental}`
    );
  }
  return webEl;
}

register('oj-table', OjTable);
