import { By } from 'selenium-webdriver';
import { DriverLike, register } from '../../';
import { OjTagCloud } from './OjTagCloud';
export { OjTagCloud };

/**
 * Retrieve an instance of [[OjTagCloud]].
 * @example
 * ```javascript
 * import { ojTagCloud } from '@oracle/oraclejet-webdriver/elements';
 * const el = await ojTagCloud(driver, By.id('my-oj-tag-cloud'));
 * ```
 * @param driver A WebDriver/WebElement instance from where the element will be
 * searched. If WebDriver is passed, the element will be searched globally in the
 * document. If WebElement is passed, the search will be relative to this element.
 * @param by The locator with which to find the element
 */
export async function ojTagCloud(driver: DriverLike, by: By): Promise<OjTagCloud> {
  const webEl = await driver.findElement(by);
  // Check that the element is of type OjTagCloud
  if (!(webEl instanceof OjTagCloud)) {
    const tagName = await webEl.getTagName();
    let supplemental = '';
    if (tagName === OjTagCloud.name) {
      supplemental = 'You likely have multiple installations of this package.';
    }
    throw Error(
      `ojTagCloud(${by}) created ${webEl.constructor.name} for <${tagName}>, but expected ${OjTagCloud.name}. ${supplemental}`
    );
  }
  return webEl;
}

register('oj-tag-cloud', OjTagCloud);
