import { By } from 'selenium-webdriver';
import { DriverLike, register } from '../../';
import { OjThematicMap } from './OjThematicMap';
export { OjThematicMap };

/**
 * Retrieve an instance of [[OjThematicMap]].
 * @example
 * ```javascript
 * import { ojThematicMap } from '@oracle/oraclejet-webdriver/elements';
 * const el = await ojThematicMap(driver, By.id('my-oj-thematic-map'));
 * ```
 * @param driver A WebDriver/WebElement instance from where the element will be
 * searched. If WebDriver is passed, the element will be searched globally in the
 * document. If WebElement is passed, the search will be relative to this element.
 * @param by The locator with which to find the element
 */
export async function ojThematicMap(driver: DriverLike, by: By): Promise<OjThematicMap> {
  const webEl = await driver.findElement(by);
  // Check that the element is of type OjThematicMap
  if (!(webEl instanceof OjThematicMap)) {
    const tagName = await webEl.getTagName();
    let supplemental = '';
    if (tagName === OjThematicMap.name) {
      supplemental = 'You likely have multiple installations of this package.';
    }
    throw Error(
      `ojThematicMap(${by}) created ${webEl.constructor.name} for <${tagName}>, but expected ${OjThematicMap.name}. ${supplemental}`
    );
  }
  return webEl;
}

register('oj-thematic-map', OjThematicMap);
