import { By } from 'selenium-webdriver';
import { DriverLike, register } from '../../';
import { OjTimeline } from './OjTimeline';
export { OjTimeline };

/**
 * Retrieve an instance of [[OjTimeline]].
 * @example
 * ```javascript
 * import { ojTimeline } from '@oracle/oraclejet-webdriver/elements';
 * const el = await ojTimeline(driver, By.id('my-oj-timeline'));
 * ```
 * @param driver A WebDriver/WebElement instance from where the element will be
 * searched. If WebDriver is passed, the element will be searched globally in the
 * document. If WebElement is passed, the search will be relative to this element.
 * @param by The locator with which to find the element
 */
export async function ojTimeline(driver: DriverLike, by: By): Promise<OjTimeline> {
  const webEl = await driver.findElement(by);
  // Check that the element is of type OjTimeline
  if (!(webEl instanceof OjTimeline)) {
    const tagName = await webEl.getTagName();
    let supplemental = '';
    if (tagName === OjTimeline.name) {
      supplemental = 'You likely have multiple installations of this package.';
    }
    throw Error(
      `ojTimeline(${by}) created ${webEl.constructor.name} for <${tagName}>, but expected ${OjTimeline.name}. ${supplemental}`
    );
  }
  return webEl;
}

register('oj-timeline', OjTimeline);
