import { By } from 'selenium-webdriver';
import { DriverLike, register } from '../../';
import { OjTrain } from './OjTrain';
export { OjTrain };

/**
 * Retrieve an instance of [[OjTrain]].
 * @example
 * ```javascript
 * import { ojTrain } from '@oracle/oraclejet-webdriver/elements';
 * const el = await ojTrain(driver, By.id('my-oj-train'));
 * ```
 * @param driver A WebDriver/WebElement instance from where the element will be
 * searched. If WebDriver is passed, the element will be searched globally in the
 * document. If WebElement is passed, the search will be relative to this element.
 * @param by The locator with which to find the element
 */
export async function ojTrain(driver: DriverLike, by: By): Promise<OjTrain> {
  const webEl = await driver.findElement(by);
  // Check that the element is of type OjTrain
  if (!(webEl instanceof OjTrain)) {
    const tagName = await webEl.getTagName();
    let supplemental = '';
    if (tagName === OjTrain.name) {
      supplemental = 'You likely have multiple installations of this package.';
    }
    throw Error(
      `ojTrain(${by}) created ${webEl.constructor.name} for <${tagName}>, but expected ${OjTrain.name}. ${supplemental}`
    );
  }
  return webEl;
}

register('oj-train', OjTrain);
