import { By } from 'selenium-webdriver';
import { DriverLike, register } from '../../';
import { OjTreeView } from './OjTreeView';
export { OjTreeView };

/**
 * Retrieve an instance of [[OjTreeView]].
 * @example
 * ```javascript
 * import { ojTreeView } from '@oracle/oraclejet-webdriver/elements';
 * const el = await ojTreeView(driver, By.id('my-oj-tree-view'));
 * ```
 * @param driver A WebDriver/WebElement instance from where the element will be
 * searched. If WebDriver is passed, the element will be searched globally in the
 * document. If WebElement is passed, the search will be relative to this element.
 * @param by The locator with which to find the element
 */
export async function ojTreeView(driver: DriverLike, by: By): Promise<OjTreeView> {
  const webEl = await driver.findElement(by);
  // Check that the element is of type OjTreeView
  if (!(webEl instanceof OjTreeView)) {
    const tagName = await webEl.getTagName();
    let supplemental = '';
    if (tagName === OjTreeView.name) {
      supplemental = 'You likely have multiple installations of this package.';
    }
    throw Error(
      `ojTreeView(${by}) created ${webEl.constructor.name} for <${tagName}>, but expected ${OjTreeView.name}. ${supplemental}`
    );
  }
  return webEl;
}

register('oj-tree-view', OjTreeView);
