import { By } from 'selenium-webdriver';
import { DriverLike, register } from '../../';
import { OjTreemap } from './OjTreemap';
export { OjTreemap };

/**
 * Retrieve an instance of [[OjTreemap]].
 * @example
 * ```javascript
 * import { ojTreemap } from '@oracle/oraclejet-webdriver/elements';
 * const el = await ojTreemap(driver, By.id('my-oj-treemap'));
 * ```
 * @param driver A WebDriver/WebElement instance from where the element will be
 * searched. If WebDriver is passed, the element will be searched globally in the
 * document. If WebElement is passed, the search will be relative to this element.
 * @param by The locator with which to find the element
 */
export async function ojTreemap(driver: DriverLike, by: By): Promise<OjTreemap> {
  const webEl = await driver.findElement(by);
  // Check that the element is of type OjTreemap
  if (!(webEl instanceof OjTreemap)) {
    const tagName = await webEl.getTagName();
    let supplemental = '';
    if (tagName === OjTreemap.name) {
      supplemental = 'You likely have multiple installations of this package.';
    }
    throw Error(
      `ojTreemap(${by}) created ${webEl.constructor.name} for <${tagName}>, but expected ${OjTreemap.name}. ${supplemental}`
    );
  }
  return webEl;
}

register('oj-treemap', OjTreemap);
