import { By } from 'selenium-webdriver';
import { DriverLike, register } from '../../';
import { OjWaterfallLayout } from './OjWaterfallLayout';
export { OjWaterfallLayout };

/**
 * Retrieve an instance of [[OjWaterfallLayout]].
 * @example
 * ```javascript
 * import { ojWaterfallLayout } from '@oracle/oraclejet-webdriver/elements';
 * const el = await ojWaterfallLayout(driver, By.id('my-oj-waterfall-layout'));
 * ```
 * @param driver A WebDriver/WebElement instance from where the element will be
 * searched. If WebDriver is passed, the element will be searched globally in the
 * document. If WebElement is passed, the search will be relative to this element.
 * @param by The locator with which to find the element
 */
export async function ojWaterfallLayout(driver: DriverLike, by: By): Promise<OjWaterfallLayout> {
  const webEl = await driver.findElement(by);
  // Check that the element is of type OjWaterfallLayout
  if (!(webEl instanceof OjWaterfallLayout)) {
    const tagName = await webEl.getTagName();
    let supplemental = '';
    if (tagName === OjWaterfallLayout.name) {
      supplemental = 'You likely have multiple installations of this package.';
    }
    throw Error(
      `ojWaterfallLayout(${by}) created ${webEl.constructor.name} for <${tagName}>, but expected ${OjWaterfallLayout.name}. ${supplemental}`
    );
  }
  return webEl;
}

register('oj-waterfall-layout', OjWaterfallLayout);
